mapper.select:
SELECT EXP.* FROM ( SELECT ROWNUM AS seq, DEP1.* FROM ( SELECT COUNT(1) OVER() AS viewCount, DEP2.* FROM (
select s_brand_name from board where boardType = '2' and s_brand_name = #{s_brand_name}
) DEP2 ) DEP1 WHERE ROWNUM <= ( TO_NUMBER(#{nowPage})* TO_NUMBER(5)) ) EXP WHERE EXP.SEQ >= ( (TO_NUMBER(#{nowPage})-1)* TO_NUMBER(5))+1


    select *
    from (
		SELECT WRITER_ID, WRITER_NAME, ROWNUM AS RNUM
		FROM writer
		where REGEXP_LIKE (WRITER_NAME, '(*)'||#{searchWord}||'(*)')
		ORDER BY WRITER_ID ASC
	) where RNUM>=#{startRow} and RNUM<=#{endRow}


select *
from(
	select ROWNUM AS RNUM , S_BRAND_NAME, TITLE, B_CONTENT, B_NO
	from BOARD
	where BOARDTYPE = 2
	and S_BRAND_NAME = #{s_brand_name}
)

where RNUM>=#{startRow} and RNUM<=#{endRow}


select *
from(
	select ROWNUM AS RNUM , S_BRAND_NAME, S_ADDRESS
	from STORE

)

where RNUM>=#{startRow} and RNUM<=#{endRow}
