mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					select * from board

						boardType=#{boardType}
						AND ${searchType} like '%'||#{keyword}||'%'

					ORDER BY b_date DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
      Update board set boardType=#{boardType},b_no=#{b_no},title=#{title},userId=#{userId},b_content=#{b_content},
      viewCount=#{viewCount},f_addr1=#{f_addr1},f_addr2=#{f_addr2},f_membercnt=#{f_membercnt},f_date=#{f_date},f_port=#{f_port},f_joincnt=#{f_joincnt},
      d_suspect=#{d_suspect},r_date=#{r_date},r_visit_date=#{r_visit_date},r_ppl_cnt=#{r_ppl_cnt},r_menu=#{r_menu},v_star=#{v_star},v_fileName=#{v_fileName},v_fileSize=#{v_fileSize},f_cnt=#{f_cnt},
      d_fileName=#{d_fileName},d_fileSize=#{d_fileSize},f_userId=#{f_userId},s_brand_name=#{s_brand_name},d_reason=#{d_reason}
	  WHERE boardType=#{boardType} AND b_no=#{b_no}

mapper.select:
SELECT COUNT(*) FROM board

boardType=#{boardType}
	AND ${searchType} like '%'||#{keyword}||'%'

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					select * from board

						boardType=#{boardType} and s_brand_name = #{s_brand_name}
						AND ${searchType} like '%'||#{keyword}||'%'

					ORDER BY ROWNUM DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

select * 
from member m, board b
where b.userid =  m.m_id AND b.userid=#{m_id}
